CREATE EXTENSION IF NOT EXISTS pgcrypto;
CREATE TABLE "public"."bounty"("id" uuid NOT NULL DEFAULT gen_random_uuid(), "contractAddress" text, "status" text NOT NULL, "issueUrl" text, "fee" numeric NOT NULL, "funderId" uuid NOT NULL, "created_at" timestamptz NOT NULL DEFAULT now(), "updated_at" timestamptz NOT NULL DEFAULT now(), PRIMARY KEY ("id") , FOREIGN KEY ("status") REFERENCES "public"."bountyStatusTypes"("value") ON UPDATE restrict ON DELETE restrict, FOREIGN KEY ("funderId") REFERENCES "public"."users"("id") ON UPDATE restrict ON DELETE restrict, UNIQUE ("id"), UNIQUE ("issueUrl"));
